mapper.update:
UPDATE tb_board
SET
    title = #{title},
    content = #{content},
    priority = #{priority}
WHERE board_no = #{boardNo}

mapper.delete:
DELETE FROM tb_board
WHERE board_no IN

    #{id}

mapper.select:
SELECT priority
FROM tb_board
WHERE board_no = #{boardNo}

mapper.update:
UPDATE tb_board
SET priority = #{priority}
WHERE board_no = #{boardNo}

update.foreach:
UPDATE tb_board
SET priority = #{item.priority}
WHERE board_no = #{item.boardNo}

mapper.update:
UPDATE tb_board
SET status_yn = #{statusYn}
WHERE board_no = #{boardNo}
