mapper.update:
UPDATE tb_userinfo
SET password = #{newPassword}
WHERE login_id = #{login_id}
  AND status_yn = 'N'
  AND withdrawal_date IS NULL

UPDATE tb_userinfo
SET
status_yn = #{status_yn},
withdrawal_date = #{withdrawal_date}
WHERE user_no = #{user_no}
AND login_id = #{login_id}
AND status_yn = 'N'
AND withdrawal_date IS NULL
