mapper.insert:
INSERT INTO tb_language_skill(
    user_no,
    language,
    level
)
VALUES (
        #{user_no},
        #{language},
        #{level}
       )

mapper.update:
UPDATE tb_language_skill
SET user_no = #{user_no},
    language = #{language},
    level = #{level}
WHERE user_no = #{user_no} AND language = #{language}

UPDATE tb_skill
SET exp_level = #{exp_level},
    skill_tool = #{skill_tool}
WHERE user_no = #{user_no} AND skill_code = #{skill_code} AND group_code = #{group_code}

mapper.select:
SELECT order_id, user_no, product_no, order_name, amount, paid_date FROM tb_payment_his WHERE user_no = #{user_no}
AND (order_name LIKE CONCAT('%', #{search}, '%') OR product_no LIKE CONCAT('%', #{search}, '%'))
ORDER BY paid_date DESC LIMIT #{limit} OFFSET #{offset}
