mapper.update:
UPDATE tb_board
SET priority = priority - 1
WHERE board_type = #{board_type}
  AND board_no != #{id}
  AND priority > #{currentPriority}
  AND priority <= #{newPriority}

UPDATE tb_board
SET priority = priority + 1
WHERE board_type = #{board_type}
  AND board_no != #{id}
AND priority < #{currentPriority}
AND priority >= #{newPriority}

mapper.select:
SELECT count(*) + 1
FROM tb_board
WHERE board_type = #{board_type}

SELECT IFNULL(MAX(board_no), 0) + 1
FROM tb_board
WHERE board_type = #{board_type}

mapper.update:
UPDATE tb_board
SET priority = priority -1
WHERE board_type = #{board_type}
	AND priority > #{priority}
	AND priority <= #{totalCount}

UPDATE tb_board
SET priority = #{priority}
WHERE board_no = #{id}

mapper.delete:
DELETE FROM tb_board
WHERE board_no IN

	#{id}
