mapper.insert:
INSERT INTO 	tb_payment_his
		(
				order_Id
		,		product_no
		,		order_name
		,		amount
		,		user_no
		,		pay_status
		,		paid_date
		) VALUES (
				#{orderId}
		 ,		#{product_no}
		 ,		#{order_name}
		 ,		#{amount}
		 ,		#{user_no}
		 ,		'1'
		 ,		NOW()
		 )

mapper.update:
UPDATE 		tb_payment_his
SET 		payment_key		 = 	#{paymentKey}
	  ,		pay_status 		 = 	'2'
	  ,  	paid_date 	     =  NOW()
WHERE 		order_Id	 	 =  #{orderId}

mapper.select:
SELECT 		product_no
FROM 		tb_payment_his
WHERE 		order_id = #{orderId}

SELECT 		sub_period
FROM 		tb_product_info
WHERE 		product_no = #{productNo}

SELECT 		user_no
FROM 		tb_payment_his
WHERE 		order_id = #{orderId}

SELECT 		expire_days
FROM 		tb_userinfo
WHERE 		user_no = #{userNo}

mapper.update:
UPDATE 		tb_userinfo
SET 		expire_days = #{newExpireDate}
WHERE 		user_no = #{userNo}
