mapper.insert:
INSERT INTO 	tb_payment_his
    (
				order_Id
		,		product_no
		,		order_name
		,		amount
		,		user_no
		,		paid_date
) VALUES (
				#{orderId}
    	,		#{product_no}
    	,		#{order_name}
    	,		#{amount}
    	,		#{user_no}
    	,		NOW()
)

mapper.update:
UPDATE 		tb_payment_his
SET 		payment_key	 	 = 	#{payment_key}
	  ,  	paid_date 	 	 =  NOW()
WHERE 		order_Id 		 = #{orderId}
AND			user_no			 = #{user_no}
AND			amount			 = #{amount}
