mapper.update:
UPDATE tb_resume_info
SET status_yn = 'Y'
WHERE resume_no = #{resume_no};

mapper.insert:
INSERT INTO tb_link_info (
	resume_no,
	link_name,
	link_url
) VALUES (
			 #{resume_no},
			 #{link_name},
			 #{link_url}
		 )

INSERT INTO tb_resume_like (
	user_no,
	resume_no
) VALUES (
			 #{user_no},
			 #{resume_no}
		 )
